mapper.select:
   
       select *
       from(
	       select
		       	/*+ INDEX_DESC(product prouduct_pk)*/
		       	rownum rn, pid, clarge, cmdedium, csmall, pno, pname, pprice
	       		from product
	       		where rownum <=#{cri.pageNum} * #{cri.amount}
	       		and clarge = #{clarge}    
	        )
	       where rn > ( #{cri.pageNum}-1 ) * #{cri.amount}

   
       select *
       from(
	       select
		       	/*+ INDEX_DESC(product prouduct_pk)*/
		       	rownum rn, pid, clarge, cmdedium, csmall, pno, pname, pprice
	       		from product
	       		where rownum <=#{cri.pageNum} * #{cri.amount}
	       		and cmdedium = #{cmdedium}
	        )
	       where rn > ( #{cri.pageNum}-1 ) * #{cri.amount}

   
       select *
       from(
	       select
		       	/*+ INDEX_DESC(product prouduct_pk)*/
		       	rownum rn, pid, clarge, cmdedium, csmall, pno, pname, pprice
	       		from product
	       		where rownum <=#{cri.pageNum} * #{cri.amount}
	       		and csmall = #{csmall}
	        )
	       where rn > ( #{cri.pageNum}-1 ) * #{cri.amount}
